={{((resource, operation, hideTools) => {
    if (resource === 'assistant' && operation === 'message') {
        return [
            { type: "main" },
            { type: "ai_memory", displayName: 'Memory', maxConnections: 1 },
            { type: "ai_tool", displayName: 'Tools' },
        ];
    }
    if (resource === 'text' && operation === 'message') {
        if (hideTools === 'hide') {
            return ["main"];
        }
        return [
            { type: "main" },
            { type: "ai_tool", displayName: 'Tools' },
        ];
    }
    return ["main"];
})($parameter.resource, $parameter.operation, $parameter.hideTools)}}

